#!/bin/sh# Name of job#SBATCH -J remc# Walltime limit (hours:mins:secs)#SBATCH -t 10:00:00# Nodes and procs#SBATCH -N 1#SBATCH -n 1# Standard error and out files#SBATCH -o outs_remc/remc.o#SBATCH -e outs_remc/remc.emodule unload gccmodule load gcc/6.2.0echo "Starting job $SLURM_JOB_ID"export LD_LIBRARY_PATH=~/lib:$LD_LIBRARY_PATHexport PATH=~/bin/$PATHmkdir -p outs_remc# Main job~/LatticeDNAOrigami/bin/latticeDNAOrigami -i remc.inp > outs_remc/remc.outechoecho "Job finished. SLURM details are:"echoqstat -f ${SLURM_JOB_ID}echoecho Finished at `date`